/*global chrome*/
import logo from './logo.svg'
import './App.css'
import React, { useState, useEffect } from 'react'
import { StyleSheet, ScrollView, SafeAreaView, StatusBar, Text } from 'react-native';
// import {useSettingsStore} from '../common/settings'


const DUMMY_DATA = [
  {
    provider_id : "user",
    utterance : "Hi!"
  },
  {
    provider_id : "iFetch",
    utterance : "Hi! My name is iFetch, how can I help you?"
  }
]

const IMAGES_ENDPOINT = "https://large.novasearch.org/farfetch_products/images/"
const MESSAGES_ENDPOINT = "https://ifetch.novasearch.org/agent/"

function Recomenadation(props) {
  const recommendations = props.message.recommendations
  const hasRecommendations = false

  const [img, setImg] = useState()
  const [index, setIndex] = useState(0)

  const fetchImage = async (imageUrl) => {
    const res = await fetch(imageUrl)
    const imageBlob = await res.blob()
    const imageObjectURL = URL.createObjectURL(imageBlob)
    setImg(imageObjectURL)
  }

  // useEffect(() => {
  //   fetchImage(0)
  // })

  // console.log(props.message)
  if (recommendations.length != 0) {
    fetchImage(recommendations[0].image_path)
    hasRecommendations = true
  }

  return (
    <div>
      {hasRecommendations ? <img src={img} alt="icons" /> : <></>}
    </div>
  )
}

function Message(props) {
  var message = props.message
  var is_user = props.message.provider_id != "iFetch"

  return (
    <div className={is_user ? 'message-user' : 'message-bot'}>
      <div className={is_user ? 'message-content-user' : 'message-content-bot'}>
        {message.utterance}
      </div>
      <div className={is_user ? 'message-timestamp-user' : 'message-timestamp-bot'}>
        {message.provider_id}
      </div>
      <recommendations message = {props.message}/>
    </div>
  )
}

function Messages(props) {
  return (
    <ScrollView>                 
      {props.messages.map((message, i) => {
        return (
          <div key={message.provider_id + i}>
            <Message message={message}/>
          </div>
        )
      })}
    </ScrollView>
  )
}

function SendMessageForm (props) {
  const [message, setMessage] = useState("")

  var handleChange = (e) => {
    setMessage(e.target.value) // TODO: Fix this!!!
    // props.handleChange(value)
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    props.handleSubmit(message)
    setMessage("")
  }

  return (
    <form
      onSubmit={handleSubmit}>
      <input
        className='text-form'
        onChange={handleChange}
        value={message}
        placeholder="Type your message and hit ENTER"
        type="text"/>
    </form>
  )
}

async function SendMessage (
  utterance, userId, sessionId,
  userAction, selectedId, 
  respondeCallback, isUpToDate=false) {

  const response = await fetch(MESSAGES_ENDPOINT, {
    method: "POST",
    body: JSON.stringify({
      utterance : utterance,
      user_id : userId, // TODO: Randomize
      session_id: sessionId, // TODO: Randomize
      user_action: userAction,
      interface_selected_product_id: selectedId 
    }),
    headers: {
      "Content-type": "application/json"
    },
    }).then((response) => response.json())
    .then((data) => {
      console.log(data)
      respondeCallback(data, utterance, isUpToDate)
    })
    .catch((err) => {
      console.log(err.message)
    })

    return response
}

function randomNumberInRange(min, max) {
  // 👇️ get number between min (inclusive) and max (inclusive)
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

function App() {
  const [messages, setMessages] = useState([])
  const [userID, setUserID] = useState(`${randomNumberInRange(0, 10000)}`)
  const [sessionID, setSessionID] = useState(`${randomNumberInRange(0, 10000)}`)

  const sendTestMessage = () => {
    const message = {
        // from: Sender.React,
        message: "Hello from React",
        uID: userID,
        sID: sessionID
    }

    const queryInfo = {
        active: true,
        currentWindow: true
    };

    chrome.tabs && chrome.tabs.query(queryInfo, tabs => {
        const currentTabId = tabs[0].id;
        chrome.tabs.sendMessage(
            currentTabId,
            message,
            (response) => {
                // setResponseFromContent(response);
                console.log(response)
            });
      });
  };

  const handleSubmit = (message) => {
    // if (!hasResponded) return
    // console.log(settings)
    const temp = {
      provider_id : "user",
      utterance : message,
      recommendations : []
    }

    setMessages([...messages, temp])
    SendMessage(message, userID, sessionID, "", "", recieveMessage)
  }

  const recieveMessage = (message, utterance, isUpToDate=false) => {
    // if (!message.has_response) return

    const temp1 = {
      provider_id : "iFetch",
      utterance : message.response,
      recommendations : message.recommendations
    }

    if (isUpToDate) {
      setMessages([...messages, temp1])
      return
    }

    const temp2 = {
      provider_id : "user",
      utterance : utterance,
      recommendations : []
    }

    setMessages([...messages, temp2, temp1])
    // console.log(messages)
  }

  useEffect(() => {
    const response = SendMessage("Hi!", userID, sessionID, "", "", recieveMessage, true)
    sendTestMessage()
  }, [])

  // console.log(value)

  return (
    <div className='chat-container'>
      <div className='title-container'>
        <h1 className='message-content-bot'>iFetch</h1>
      </div>
      <SafeAreaView style={styles.container}>
        <Messages messages={messages}></Messages>
      </SafeAreaView>
      <div className='form-container'>
        <SendMessageForm handleSubmit = {handleSubmit}/>
      </div>
    </div>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    height: 450
  }
});

export default App;
