import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';

const DUMMY_DATA = [
  {
    provider_id : "user",
    utterance : "Hi!"
  },
  {
    provider_id : "iFetch",
    utterance : "Hi! My name is iFetch, how can I help you?"
  }
]

const IMAGES_ENDPOINT = "https://large.novasearch.org/farfetch_products/images/"
const MESSAGES_ENDPOINT = "https://ifetch.novasearch.org/agent/"

function Message(props) {
  var message = props.message
  var is_user = props.message.provider_id != "iFetch"

  return (
    <div className={is_user ? 'message-orange' : 'message-blue'}>
      <div className='message-content'>
        {message.utterance}
        </div>
      <div className={'message-timestamp-left'}>
        {message.provider_id}
        </div>
    </div>
  )
}

function Messages(props) {
  return (
    <div className="container">                 
      {props.messages.map((message, i) => {
        return (
          <li key={message.provider_id + i}>
            <Message message={message}/>
          </li>
        )
      })}
    </div>
  )
}

function SendMessageForm (props) {
  const [message, setMessage] = useState("")

  var handleChange = (e) => {
    setMessage(e.target.value) // TODO: Fix this!!!
    // props.handleChange(value)
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    props.handleSubmit(message)
    setMessage("")
  }

  return (
    <form
      onSubmit={handleSubmit}>
      <input
        className='text-form'
        onChange={handleChange}
        value={message}
        placeholder="Type your message and hit ENTER"
        type="text"/>
    </form>
  )
}


function App() {
  const [messages, setMessages] = useState(DUMMY_DATA)
  const [hasResponded, setHasResponded] = useState(false)

  const handleSubmit = (message) => {
    // if (!hasResponded) return

    const temp = {
      provider_id : "user",
      utterance : message
    }
    setMessages([...messages, temp])
    console.log(messages)
  }

  const SendMessage = (
    utterance, userId, sessionId,
    userAction, selectedId, 
    respondeCallback) => {

    const response = fetch(MESSAGES_ENDPOINT, {
      method: "POST",
      body: JSON.stringify({
        utterance : utterance,
        user_id : userId, // TODO: Randomize
        session_id: sessionId, // TODO: Randomize
        user_action: userAction,
        interface_selected_product_id: selectedId 
      }),
      headers: {
        "Content-type": "application/json"
      },
      }).then((response) => response.json())
      .then((data) => {
        console.log(data)
        respondeCallback(data)
      })
      .catch((err) => {
        console.log(err.message)
      })

      return response
  }

  const recieveMessage = (message) => {
    if (!message.has_response) return

    const temp = {
      provider_id : "user",
      utterance : message
    }
    setMessages([...messages, temp])

  }

  useEffect(() => {
    const response = SendMessage("Hi!", "1", "123", "", "", recieveMessage)

    // const response = fetch(MESSAGES_ENDPOINT, {
    //   method: "POST",
    //   body: JSON.stringify({
    //     utterance : "Hi!",
    //     user_id : "1", // TODO: Randomize
    //     session_id: "123", // TODO: Randomize
    //     user_action: "",
    //     interface_selected_product_id: "" 
    //   }),
    //   headers: {
    //     "Content-type": "application/json"
    //   },
    //   }).then((response) => response.json())
    //   .then((data) => {
    //     console.log(data)
    //     // setPosts(data);
    //   })
    //   .catch((err) => {
    //     console.log(err.message)
    //   })
  }, [])

  return (
    <div>
      <div>
        <Messages messages={messages}></Messages>
      </div>
      <div>
        <SendMessageForm handleSubmit = {handleSubmit}/>
      </div>
    </div>
  );
}

export default App;
