import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

const DUMMY_DATA = [
  {
    provider_id : "user",
    utterance : "Hi!"
  },
  {
    provider_id : "chatbot",
    utterance : "Hi! My name is iFetch, how can I help you?"
  }
]

function Message(props) {
  var message = props.message

  return (
    <li key={message.id}>
    <div>
      {message.provider_id}
    </div>
    <div>
      {message.utterance}
    </div>
  </li>
  )
}

function Messages(props) {
  return (
    <ul className="message-list">                 
      {props.messages.map(message => {
        return (
          <Message message={message}/>
        )
      })}
    </ul>
  )
}



function SendMessageForm (props) {
  const [message, setMessage] = useState("Type your message and hit ENTER")
  const [handleChange, setHandleChange] = useState((value) => {
    setMessage(value)
    // props.handleChange(message)
  })

  return (
    <form
      className="send-message-form">
      <input
        onChange={handleChange}
        value={message}
        placeholder="Type your message and hit ENTER"
        type="text" />
    </form>
  )
}

function App() {
  return (
    <div>
      <div>
        <Messages messages={DUMMY_DATA}></Messages>
      </div>
      <div>
        <SendMessageForm/>
      </div>
    </div>
  );
}

export default App;
