import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

const DUMMY_DATA = [
  {
    provider_id : "user",
    utterance : "Hi!"
  },
  {
    provider_id : "chatbot",
    utterance : "Hi! My name is iFetch, how can I help you?"
  }
]

function Message(props) {
  var message = props.message

  return (
    <div>
      <div>
        {message.provider_id}
      </div>
      <div>
        {message.utterance}
      </div>
    </div>
  )
}

function Messages(props) {
  return (
    <ul>                 
      {props.messages.map((message, i) => {
        return (
          <li key={message.provider_id + i}>
            <Message message={message}/>
          </li>
        )
      })}
    </ul>
  )
}



function SendMessageForm (props) {
  const [message, setMessage] = useState("Type your message and hit ENTER")

  const [handleChange, setHandleChange] = useState((value) => {
    console.log(value)
    setMessage(value)
    // props.handleChange(value)
  })

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(e)
    props.handleSubmit(e)
  }

  return (
    <form
      onSubmit={handleSubmit}>
      <input
        onChange={handleChange}
        value={message}
        placeholder="Type your message and hit ENTER"
        type="text" />
    </form>
  )
}

function App() {
  const [messages, setMessages] = useState(DUMMY_DATA)
  const handleSubmit = (message) => {
    // const temp = messages
    // temp.push({
    //   provider_id : "user ",
    //   utterance : message
    // })
    // console.log(temp)
    // setMessages(temp)
  }

  return (
    <div>
      <div>
        <Messages messages={messages}></Messages>
      </div>
      <div>
        <SendMessageForm handleSubmit = {handleSubmit}/>
      </div>
    </div>
  );
}

export default App;
