import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

const DUMMY_DATA = [
  {
    provider_id : "user",
    utterance : "Hi!"
  },
  {
    provider_id : "chatbot",
    utterance : "Hi! My name is iFetch, how can I help you?"
  }
]

function Message(props) {
  var message = props.message

  return (
    <li key={props.id}>
    <div>
      {message.provider_id}
    </div>
    <div>
      {message.utterance}
    </div>
  </li>
  )
}

function Messages(props) {
  return (
    <ul>                 
      {props.messages.map((message, i) => {
        console.log(i)
        return (
          <Message message={message} id={message.provider_id + i}/>
        )
      })}
    </ul>
  )
}



function SendMessageForm (props) {
  const [message, setMessage] = useState("Type your message and hit ENTER")
  const [handleChange, setHandleChange] = useState((value) => {
    setMessage(value)
    // props.handleChange(value)
  })
  const [handleSubmit, setHandleSubmit] = useState((value) => {
    props.handleSubmit(value)
  })

  return (
    <form
      onSubmit={handleSubmit}>
      <input
        onChange={handleChange}
        value={message}
        placeholder="Type your message and hit ENTER"
        type="text" />
    </form>
  )
}

function App() {
  const [messages, setMessages] = useState(DUMMY_DATA)
  const handleSubmit = (message) => {
    const temp = messages
    temp.push({
      provider_id : "user ",
      utterance : message
    })
    setMessages(temp)
  }

  return (
    <div>
      <div>
        <Messages messages={messages}></Messages>
      </div>
      <div>
        <SendMessageForm handleSubmit = {handleSubmit}/>
      </div>
    </div>
  );
}

export default App;
